<?php/** * Created by PhpStorm. * User: Andrew * Date: 26.07.2017 * Time: 10:03 */namespace NetworkPosts\Components;require_once 'NetsPostsUtils.php';class NetsPostsDBQuery {	protected $wpdb;	protected $wp_tables;	protected $estore_tables;	protected $taxonomy_type = ['category'];	private function __construct( $db ) {		$this->wpdb = $db;	}	public function init( $blog_id ) {		if ( $blog_id == 1 ) {			$this->wp_tables = array(				'posts_table' => $this->wpdb->base_prefix . "posts",				'term_relationship_table' => $this->wpdb->base_prefix . "term_relationships",				'term_taxonomy_table' => $this->wpdb->base_prefix . "term_taxonomy",				'term_table' => $this->wpdb->base_prefix . "terms",				'post_meta' => $this->wpdb->base_prefix . 'postmeta'			);			$this->estore_tables = array(				'product_table' => $this->wpdb->base_prefix . "wp_eStore_tbl",				'category_table' => $this->wpdb->base_prefix . "wp_eStore_cat_tbl",				'category_relationships' => $this->wpdb->base_prefix . "wp_eStore_cat_prod_rel_tbl"			);		} else {			$this->wp_tables = array(				'posts_table' => $this->wpdb->base_prefix . "{$blog_id}_posts",				'term_relationship_table' => $this->wpdb->base_prefix . "{$blog_id}_term_relationships",				'term_taxonomy_table' => $this->wpdb->base_prefix . "{$blog_id}_term_taxonomy",				'term_table' => $this->wpdb->base_prefix . "{$blog_id}_terms",				'post_meta' => $this->wpdb->base_prefix . "{$blog_id}_postmeta"			);			$this->estore_tables = array(				'product_table' => $this->wpdb->base_prefix . "{$blog_id}_wp_eStore_tbl",				'category_table' => $this->wpdb->base_prefix . "{$blog_id}_wp_eStore_cat_tbl",				'category_relationships' => $this->wpdb->base_prefix . "{$blog_id}_wp_eStore_cat_prod_rel_tbl"			);		}	}	public function set_taxonomy_type( $type ){		$this->taxonomy_type = $type;	}	public function get_formatted_category_post_ids( $categories, $type = 'include', $include_posts ) {		$ids = $this->get_category_post_ids( $categories, $include_posts );		$column_name = 'ID';		if ( $type == 'include' ) {			return format_inclusion( $this->wp_tables['posts_table'], $column_name, $ids );		} else {			return format_exclusion( $this->wp_tables['posts_table'], $column_name, $ids );		}	}	public function get_category_post_ids( $categories, $include_posts = null ){		if ( is_array( $categories ) ) {			$terms_table = $this->wp_tables['term_table'];			$term_relationship_table = $this->wp_tables['term_relationship_table'];			$categories = array_map( function( $name ) use ($terms_table){				return 'LOWER(' . $terms_table . ".slug) LIKE LOWER('$name')";			}, $categories );			$category_expr = '(' . join( ' OR ', $categories ) . ')';			if( $include_posts ){				$include_posts = array_map(function( $item ) use ($term_relationship_table){					return $term_relationship_table . '.object_id = ' . $item;				}, $include_posts );				$include_posts_expr = '(' . join( ' AND ', $include_posts ) . ')';				$category_expr .= ' AND ' . $include_posts_expr;			}			$select = $term_relationship_table . '.object_id AS ID';			return $this->query_categories( $select, $category_expr );		}		return null;	}	public function get_post_categories( $post_id ) {		$terms_table              = $this->wp_tables['term_table'];		$terms_relationship_table = $this->wp_tables['term_relationship_table'];		$terms_taxonomy           = $this->wp_tables['term_taxonomy_table'];		return $this->query_categories( "$terms_table.name as cat_name, $terms_table.term_id as cat_id",			"$terms_relationship_table.object_id = $post_id");	}	protected function query_categories( $select, $where ){		$terms_table              = $this->wp_tables['term_table'];		$terms_relationship_table = $this->wp_tables['term_relationship_table'];		$terms_taxonomy           = $this->wp_tables['term_taxonomy_table'];		$taxonomy_types = array();		foreach( $this->taxonomy_type as $type ) {			$type       = strtolower( $type );			switch ( $type ) {				case 'tag':					$taxonomy_type_value = 'post_tag';					break;				case 'category':					$taxonomy_type_value = 'category';					break;				case 'product_tag':					$taxonomy_type_value = 'product_tag';					break;				case 'product_category':					$taxonomy_type_value = 'product_cat';					break;				default:					$taxonomy_type_value = 'category';			}			$taxonomy_types[] = $taxonomy_type_value;		}		$taxonomy_type_expr = " AND " . format_inclusion( $terms_taxonomy, "taxonomy", $taxonomy_types, false );		return $this->wpdb->get_results( "SELECT $select								  FROM $terms_table								  LEFT JOIN $terms_relationship_table ON $terms_relationship_table.term_taxonomy_id = $terms_table.term_id								  LEFT JOIN $terms_taxonomy ON $terms_taxonomy.term_id = $terms_table.term_id								  WHERE $where $taxonomy_type_expr", ARRAY_A );	}	public function get_posts( $args ) {		$PostsTable = $this->wp_tables['posts_table'];		$MetaTable = $this->wp_tables['post_meta'];		$meta_full_condition = '';		$meta_keys = $args['meta_keys'] ? $args['meta_keys'] : array();		$extra_post_meta = $args['include_meta'] ? $args['include_meta'] : array();		$meta_keys = array_merge( $meta_keys, $extra_post_meta );		if(count( $meta_keys )){			$meta_key_conditions = [];			foreach ($meta_keys as $key){				$meta_key_conditions[] = "$MetaTable.meta_key = '$key'";			}			$meta_full_condition = 'AND (' . join(' OR ', $meta_key_conditions) . ')';			$join_query = " LEFT JOIN $MetaTable ON ($MetaTable.post_id = $PostsTable.ID)";			$meta_columns = ", $MetaTable.meta_key, $MetaTable.meta_value ";		}		else{			$join_query = "";			$meta_columns = "";		}		$ids = $args['ids'];		$post_type_search = @$args['post_type_search'];		$old = $args['old'];		$limit = @$args['limit'];		if ( $args['is_random'] ) {			if ( @$args['paginate'] ) {				return $this->wpdb->get_results( $this->wpdb->prepare(					"SELECT $PostsTable.ID, $PostsTable.post_title, $PostsTable.post_excerpt, $PostsTable.post_content, $PostsTable.post_author, $PostsTable.post_date,                     $PostsTable.post_type $meta_columns				 FROM $PostsTable $join_query WHERE $PostsTable.post_status = %s $ids  AND $post_type_search $meta_full_condition $old  $limit", 'publish' ), ARRAY_A );			} else {				return $this->wpdb->get_results( $this->wpdb->prepare(					"SELECT $PostsTable.ID, $PostsTable.post_title, $PostsTable.post_excerpt, $PostsTable.post_content, $PostsTable.post_author,                         $PostsTable.post_date, $PostsTable.post_type $meta_columns				 FROM $PostsTable $join_query  WHERE $PostsTable.post_status = %s $ids  AND $post_type_search $meta_full_condition $old ORDER BY RAND() $limit", 'publish' ), ARRAY_A );			}		} else {			return $this->wpdb->get_results( $this->wpdb->prepare(				"SELECT $PostsTable.ID, $PostsTable.post_title, $PostsTable.post_excerpt, $PostsTable.post_content, $PostsTable.post_author, $PostsTable.post_date,                  $PostsTable.post_type $meta_columns			 FROM $PostsTable $join_query WHERE $PostsTable.post_status = %s $ids  AND $post_type_search $meta_full_condition $old $limit", 'publish' ), ARRAY_A );		}	}	public function get_formatted_estore_category_post_ids( $categories, $type = 'include', $include_posts = null ) {		$ids = $this->get_estore_category_post_ids( $categories, $include_posts );		$column_name = 'id';		if ( $type == 'include' ) {			return format_inclusion( $this->estore_tables['product_table'], $column_name, $ids );		} else {			return format_exclusion( $this->estore_tables['product_table'], $column_name, $ids );		}	}	public function get_estore_category_post_ids( $taxonomy, $include_posts = null ) {		global $wpdb;		if ( strpos( $taxonomy, ',' ) > 0 ) {			$categories = explode( ',', $taxonomy );		} else {			$categories = [ $taxonomy ];		}		$estore_cat_array = array();		$result_ids = array();		foreach ( $categories as $category ) {			$cat_id = $wpdb->get_var( $wpdb->prepare( "SELECT cat_id FROM {$this->estore_tables['category_table']} WHERE LOWER(cat_name) = LOWER('%s') ", trim( $category ) ) );			if ( $cat_id ) {				$estore_cat_array[] = $cat_id;			}		}		$include = '';		if ( $include_posts ) {			$include = format_inclusion( $this->estore_tables['category_relationships'], 'prod_id', $include_posts );			$include = " AND $include";		}		foreach ( $estore_cat_array as $estore_category ) {			$estore_post_ids = $wpdb->get_results( $wpdb->prepare( "SELECT prod_id FROM {$this->estore_tables['category_relationships']} WHERE cat_id = %s $include", $estore_category ), ARRAY_A );			if ( ! empty( $estore_post_ids ) ) {				$result_ids = array_merge_recursive( $result_ids, $estore_post_ids );			}		}		return $result_ids;	}	public function get_estore_products( $is_random, $paginate, $formatted_ids = '', $limit = '' ) {		if ( ! empty( $formatted_ids ) ) {			$estore_ids = ' WHERE ' . $formatted_ids;		}		$EStoreTable = $this->estore_tables['product_table'];		if ( $is_random ) {			if ( $paginate ) {				return $this->wpdb->get_results( "SELECT $EStoreTable.id AS ID,'estore' AS post_type, $EStoreTable.name AS post_title, $EStoreTable.price AS price,                                                         $EStoreTable.description AS post_content, $EStoreTable.author_id AS post_author,                                                         $EStoreTable.product_url AS guid, $EStoreTable.thumbnail_url                                                  FROM $EStoreTable $estore_ids $limit;", ARRAY_A );			} else {				return $this->wpdb->get_results( "SELECT $EStoreTable.id AS ID,'estore' AS post_type, $EStoreTable.name AS post_title, $EStoreTable.price AS price,                                                         $EStoreTable.description AS post_content, $EStoreTable.author_id AS post_author, $EStoreTable.product_url AS guid,                                                         $EStoreTable.thumbnail_url                                                  FROM $EStoreTable $estore_ids ORDER BY RAND() $limit;", ARRAY_A );			}		} else {			return $this->wpdb->get_results( "SELECT $EStoreTable.id AS ID,'estore' AS post_type, $EStoreTable.name AS post_title, $EStoreTable.price AS price,                                                     $EStoreTable.description AS post_content, $EStoreTable.author_id AS post_author, $EStoreTable.product_url AS guid,                                                     $EStoreTable.thumbnail_url                                              FROM $EStoreTable $estore_ids $limit;", ARRAY_A );		}	}	public function is_estore_installed() {		$result = $this->wpdb->get_results( "SHOW TABLES LIKE '" . $this->wpdb->base_prefix . "wp_estore_tbl'", ARRAY_A );		return count( $result ) == 1;	}	public function is_woocommerce_installed() {		$result = $this->wpdb->get_results( "SHOW TABLES LIKE '" . $this->wpdb->base_prefix . "woocommerce_termmeta'", ARRAY_A );		return count( $result ) == 1;	}	public function create_orderby_query_path( $column ) {		return " ORDER BY $column ";	}	public function create_by_date_query_path( $column, $days ) {		return "$column >= DATE_SUB(CURRENT_DATE(), INTERVAL $days DAY)";	}	public function get_skipped_ids( $categories, $count ) {		$excluded = array();		if ( $count > 0 ) {			foreach ( $categories as $category ) {				$posts = $this->get_category_post_ids( array( $category ) );				$posts_count = count( $posts );				if ( $posts_count > $count ) {					$temp = array_slice( $posts, count( $posts ) - $count );				} else {					$temp = $posts;				}				$excluded = array_merge( $excluded, $temp );			}		}		return $excluded;	}	public function get_blogs( $is_random, $is_paginate, $include, $exclude ) {		$BlogsTable = $this->wpdb->base_prefix . 'blogs';		if ( $is_random ) {			if ( $is_paginate ) {				return $this->wpdb->get_col( $this->wpdb->prepare(					"SELECT blog_id FROM $BlogsTable WHERE public = %d AND archived = %d AND mature = %d AND spam = %d AND deleted = %d $include $exclude  ", 1, 0, 0, 0, 0				) );			} else {				return $this->wpdb->get_col( $this->wpdb->prepare(					"SELECT blog_id FROM $BlogsTable WHERE public = %d AND archived = %d AND mature = %d AND spam = %d AND deleted = %d $include $exclude ORDER BY RAND() ", 1, 0, 0, 0, 0				) );			}		} else {			return $this->wpdb->get_col( $this->wpdb->prepare(				"SELECT blog_id FROM $BlogsTable WHERE public = %d AND archived = %d AND mature = %d AND spam = %d AND deleted = %d $include $exclude  ", 1, 0, 0, 0, 0			) );		}	}	public function get_table_name( $table ){		return isset($this->wp_tables[$table]) ? $this->wp_tables[$table] : '';	}	public static function new_instance( $db ) {		return new NetsPostsDBQuery( $db );	}}