<?php/** * Created by PhpStorm. * User: Admin * Date: 04.04.2017 * Time: 8:30 */use NetworkPosts\Components\NetsPostsHtmlHelper;if ( ! defined( 'POST_VIEWS_PATH' ) ) {	die();}$html.= '<header class="entry-header">'; //erickson/** * Coloca categoria acima do título do post * Erickson Zanon em 17/10/2018 *  * Início */if ( isset( $the_post['categories'] ) ) {	//$html .= '<div class="netsposts-categories" style="margin-bottom: 5px; ' . $width . '">';        $html .=  '<div class="entry-categories clearfix">'                . '<span class="meta-categories clearfix">';	foreach ( $the_post['categories'] as $category ) {		$html .= NetsPostsHtmlHelper::create_category_link( $blog_url, $category['cat_id'], $category['cat_name'], $open_link_in_new_tab );	}	$html .= '</span></div>';}/** * Fim */if ( $shortcode_mgr->get_boolean( 'titles_only' ) ) {	$title_class = 'netsposts-post-titles-only';} else {	$title_class = 'netsposts-posttitle';}$temp = sanitize_quotes( $shortcode_mgr->get( 'wrap_title_start' ) );$html .= htmlspecialchars_decode( $temp );/* * This code creates title link */$title_color  = $shortcode_mgr->get( 'title_color' );$title_length = $shortcode_mgr->get( 'title_length' );$title = ShortenText( $the_post['post_title'], $title_length );$title_style = 'color: ' . $title_color . ';';$html.= '<h2 class="entry-title">'; //ericksonif ( $shortcode_mgr->get_boolean( 'include_link_title' ) ) {	if ( !isset( $exclude_title_links) || ! array_has_value( $the_post['ID'], $exclude_title_links ) ) {		$html .= NetsPostsHtmlHelper::create_link($the_post['guid'], $title, $open_link_in_new_tab, 'netsposts-posttitle-link');	} else {		$html .= NetsPostsHtmlHelper::create_span($title, $title_class, $title_style );	}} else {	$html .= NetsPostsHtmlHelper::create_span($title, $title_class, $title_style );}$html.= "</h2>\n"; //erickson$html .= htmlspecialchars_decode( $shortcode_mgr->get( 'wrap_title_end' ) );$html.= "</header>\n"; //erickson